	jobsclosures <<{ jobsmap.put("testrun"+String.valueOf(i),build( "test2",NUMBER : String.valueOf(i*1000)))}
}


guard {
    parallel (
        jobsclosures      
    )
} rescue {
}

sleep(10000)

}
